const reducer=(state=initialState,action)=>{
    switch(action.type){
        case LOG_IN_REQUEST:
            return{
                    ...state,
                    logInLoading:true,
                    logInError:null,
                    logInDone:false

            }
            case LOG_IN_SUCCESS:
                return{
                        ...state,
                        logInLoading:false,

                        logInDone:true,
                        // me:{...action.data,nickname:'dongzero'}

                        me:dummyUser(action.data)

                }

                case LOG_IN_FAILURE:
                    return{
                            ...state,
                            logInLoading:false,
                            logInError:action.error,

                    }


            case LOG_OUT_SUCCESS:
                return{
                        ...state,
                        logOutLoading:false,

                        logOutDone:true,
                        me:null,
                }

            case LOG_OUT_REQUEST:
                return{
                        ...state,
                        logOutLoading:true,
                        logOutDone:false,
                        logOutError:null
                }

            case LOG_OUT_FAILURE:
                        return{
                            ...state,
                            logOutLoading:false,
                        logOutError:action.error,
                        }

                        case SIGN_UP_REQUEST:

                            return{
                                ...state,
                                signUpLoading:true,
                                signUpDone:false,
                                signUpError:null
                        }

                          case SIGN_UP_SUCCESS:
                            return{
                                ...state,
                                signUpLoading:false,

                                signUpDone:true,

                        }
                          case SIGN_UP_FAILURE:
                            return{
                                ...state,
                                signUpLoading:false,
                                signUpError:action.error,
                            }
                            case CHANGE_NICKNAME_REQUEST:

                                return{
                                    ...state,
                                    changeNicknameLoading:true,
                                    changeNicknameDone:false,
                                    changeNicknameError:null
                            }

                              case CHANGE_NICKNAME_SUCCESS:
                                return{
                                    ...state,
                                    changeNicknameLoading:false,

                                    changeNicknameDone:true,

                            }
                              case CHANGE_NICKNAME_FAILURE:
                                return{
                                    ...state,
                                    changeNicknameLoading:false,
                                    changeNicknameError:action.error,
                                }
                            case ADD_POST_TO_ME:
                                return{
                                    ...state,
                                    me:{
                                        ...state.me, 
                                        posts:[
                                            {id:action.data},...state.me.Posts
                                        ]
                                    }
                                }
                                case REMOVE_POST_OF_ME:
                                    return{
                                        ...state,
                                        me:{
                                            ...state.me, 
                                            Posts:state.me.Posts.filter((v)=>v.id!==action.data)
                                        }
                                    }


        default:
            return state
    }
}
export default reducer
