import { Result } from 'antd';
import { actionChannel, all, delay, fork, put, takeLatest} from 'redux-saga/effects'
import signup from '../pages/signup';

import {
    FOLLOW_FAILURE,
    FOLLOW_REQUEST,
    FOLLOW_SUCCESS,
    LOG_IN_FAILURE,
    LOG_IN_REQUEST,
    LOG_IN_SUCCESS,
    LOG_OUT_FAILURE,
    LOG_OUT_REQUEST,
    LOG_OUT_SUCCESS,
    SIGN_UP_FAILURE,
    SIGN_UP_REQUEST,
    SIGN_UP_SUCCESS,
    UNFOLLOW_FAILURE,
    UNFOLLOW_REQUEST,
    UNFOLLOW_SUCCESS,
  } from '../reducers/user';


function logInAPI(){
    // return axios.post('/api/post')
}

function* login(action){
    try{
        yield delay(1000);
        yield put({
            type:LOG_IN_SUCCESS,
            data:action.data
        })
    }catch(err){
        yield put({
            type:LOG_IN_FAILURE,
            error:err.response.data,

        })
    }

}

function logOutAPI(){
    // return axios.post('/api/post')
}

function* logout(){
    try{
        yield delay(1000);
        yield put({
            type:LOG_OUT_SUCCESS,
        })
    }catch(err){
        yield put({
            type:LOG_OUT_FAILURE,
            error:err.response.data,

        })
    }

}

function signUpAPI(){
    // return axios.post('/api/post')
}


function* signUp(){
    try{
        yield delay(1000);
        yield put({
            type:SIGN_UP_SUCCESS,
        })
    }catch(err){
        yield put({
            type:SIGN_UP_FAILURE,
            error:err.response.data,

        })
    }

}

function* watchLogIn(){
    yield takeLatest(LOG_IN_REQUEST,login)

}

function* watchLogOut(){
    yield takeLatest(LOG_OUT_REQUEST,logout)
}

function* watchSignUp(){
    yield takeLatest(SIGN_UP_REQUEST,signUp)
}


export default function* userSaga(){
    yield all([
        fork(watchLogIn),
        fork(watchLogOut),
        fork(watchSignUp),
    ])
}
