import React,{useState,useCallback} from 'react'
import { Form,Input,Button } from 'antd';
import Link from 'next/link'





const LoginFrom=({setIsLoggedIn}) =>{
    const [id,setId]=useState('');
    const [password,setPassword]=useState('')

    const onChangeId=useCallback((e)=>{
        setId(e.target.value)
    },[])

    const onChangePassword=useCallback((e)=>{
        setPassword(e.target.value)

    },[])

    const onSubmitForm=useCallback(()=>{
        console.log(id,password)
        setIsLoggedIn(true)
    },[id,password])




    return (
        //리액트가 새로고침되면 원래랑 다른부분이 리렌더링된다
        <Form onFinish={onSubmitForm}>
        <div>
            <label htmlFor='user-id'>아이디 </label>
            <br></br>
            <Input name="user-id" value={id} onChange={onChangeId} required></Input>
        </div>
        <div>
            <label htmlFor='user-password'>비밀번호 </label>
            <br></br>
            <Input name="user-password" htmlType="password" value={password} onChange={onChangePassword} required></Input>
        </div> 
        <div style={{marginTop:'10px'}}>
            <Button type="primary" htmlType='submit' loading={false} >로그인</Button>
            <Link href="/signup"><a><Button>회원가입</Button></a></Link>
        </div>
        </Form>
    )
}

export default LoginFrom
