import {all,call,fork,put,take, takeEvery, takeLatest,delay} from 'redux-saga/effects'

import postSaga from './post'
import userSaga from './user'

import axios from 'axios'

export default function* rootSaga(){
     yield all([
        fork(postSaga),
        fork(userSaga),
     ])
}
